//       dispatch({
//         type: "UPDATE_CONTACT",
//         data: value
//       });
//     },
//     remove: data => {
//       dispatch({
//         type: "DELETE_CONTACT",
//         id: data
//       });
//     }
//   };
// };



export default connect(mapStateToProps)(Contact);
