import uuid from 'uuid';
import { ADD_CONTACT, DELETE_CONTACT, EDIT_CONTACT} from '../actions/types';

let contacts = [
    {
        id: uuid(),
        name: "seif Guirat ",
        phone: 21145265,
        email: "seifGuirat@gmail.com"
    },
    {
        id: uuid(),
        name: "sabrina mahdi",
        phone: 24145265,
        email: "sabrinamahdi@gmail.com"
    },
    {
        id: uuid(),
        name: "maha banour",
        phone: 22145265,
        email: "mahabanour@gmail.com"
    }
]

const ContactReducer = (state = {contacts:[]}, action) => {
    switch (action.type) {
        case GET_CONTACTS:
            return {...state, contacts: action.payload};





        case ADD_CONTACT:
            return [...state, action.newContact]

        case DELETE_CONTACT:
            return state.filter(el => el.id !== action.payload);


        case EDIT_CONTACT:
            return state.map(el => (el.id === action.payload.id ? action.payload : el));


        default:
            return state;
    }
}

export default ContactReducer;
