import { ADD_CONTACT, DELETE_CONTACT,EDIT_CONTACT } from './types';




export const onAddContact = (data) => {
    return {
        type: ADD_CONTACT,
        newContact: data
    }
}

export const deleteContact = (id) => {
    return {
        type: DELETE_CONTACT,
        payload: id
    }
}


export const ModifyContact = (data) =>{
    return{
        type: EDIT_CONTACT,
        payload: id
    }
}
