import { ADD_ITEM , CHANGE_INPUT} from './types';
import { DELETE_ITEM } from './types';
import { COMPLETE_ITEM} from './types';
import {EDIT_ITEM} from './types';



export const AddItem = itemValue => ({
  type: ADD_ITEM,
  payload: itemValue,
});

export const DeleteItem = id => ({
  type: DELETE_ITEM,
  payload: id,
});

export const CompleteItem = id => ({
  type: COMPLETE_ITEM,
  payload: id,
});

export const EditItem = modifiedItem => ({
  type: EDIT_ITEM,
  payload: modifiedItem,
});

export const ChangeInput = itemToChange => ({
  type: CHANGE_INPUT,
  payload: itemToChange,
});
