    switch (action.type) {
        case GET_CONTACTS:
            return action.payload;
        // case ADD_CONTACT:
        //     return [...state, action.newContact]

        // case DELETE_CONTACT:
        //     return state.filter(el => el.id !== action.payload);


        // case EDIT_CONTACT:
        //     return state.map(el => (el.id === action.payload.id ? action.payload : el));


        default:
