import { ADD_CONTACT, DELETE_CONTACT,EDIT_CONTACT,GET_CONTACTS } from './types';
import axios from 'axios';


export const getcontacts = () => dispatch => {
    axios.get("/contacts")
        .then(res => res.data)
        .then(contact => dispatch({
                type:GET_CONTACTS,
                payload: contact
            })
    )
}




export const onAddContact = (data) => dispatch => {
    return {
        type: ADD_CONTACT,
        newContact: data
    }
}

export const deleteContact = (id) => dispatch => {
    return {
        type: DELETE_CONTACT,
        payload: id
    }
}


export const ModifyContact = (data) => dispatch =>{
    return{
        type: EDIT_CONTACT,
        payload: data
    }
}
