import { ADD_CONTACT, DELETE_CONTACT } from './types';




export const onAddContact = (data) => {
    return {
        type: ADD_CONTACT,
        newContact: data
    }
}

export const deleteContact = id => {
    return {
        type: DELETE_CONTACT,
        payload: id
    }
}
