import React from "react";
import uuid from 'uuid';
import { connect } from "react-redux";
// import {deleteContact} from  '../actions/action';
import ModifyContact from '../components/ModifyContact';



const ContactCard = ({ contact, deleteContact }) => {
  return (
    <div className="contact-card">
      <div className="contact-card-item">
        <h3>name: </h3>
        <h3>{contact.name} </h3>
      </div>
      <div className="contact-card-item">
        <span>phone_number: </span>
        <span>{contact.phone_number} </span>
      </div>
      <div className="contact-card-item">
        <span>email: </span>
        <span>{contact.email} </span>
      </div>

      <div className="contact-card-buttons">
        <input type="button" value="Edit Contact" /> 
        {/* <ModifyContact el={contact}/> */}
        <input type="button" value="Delete Contact" onClick={()=>this.props.deletecontacts(contact.id)}/>
      </div>
    </div>
  );
};


// const mapDispatchToProps = dispatch => {
//   return {
//       deleteContact: id => dispatch(deleteContact(id)),
//   }
// }

export default connect(null, {deletecontacts})(ContactCard);
