import React, { Component } from 'react';
import { connect } from 'react-redux';
import uuid from 'uuid';
import { DeleteItem } from '../actions/action';
import { CompleteItem } from '../actions/action';
import {ChangeInput} from '../actions/action';

class AddList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            term: "",
            edit: false,
        }
    }

    render() {
        return (
            <div className="App">
                <div className="AddList">
                    {
                    this.props.todos.items.map((el, i) =>
                        <div key={i} className="ToDoItem">
                            <button className="button-complete" onClick={() => this.props.complete(el.id)} >{el.complete ? 'Undo' : 'Complete'}</button>
                            <button className="button-delete" onClick={() => this.props.delete(el.id)} >Delete</button>
                            <button className="edit-btn" onClick={()=> this.props.changeInput(el)}>Edit</button>
                            <h1 className={el.complete === true ? "onclick_done" : ""}>{el.term}</h1>
                        </div>)
                        }
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        todos: state.todosReducer
    }
}

function mapDispatchToProps(dispatch) {
    return {
        delete: (x) => dispatch(DeleteItem(x)),
        complete: (x) => dispatch(CompleteItem(x)),
        changeInput: x => dispatch(ChangeInput(x)),
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(AddList);
